var exec = require('child_process').exec;

module.export = (filepath) => {

  return new Promise(function(resolve, reject) {
    var cmd = 'soffice --headless --convert-to pdf ' + filepath;

    exec(cmd, function(error, stdout, stderr) {
      if(error) {
        reject(error)
      } else {
        resolve()
      }
    });
  });

}
